import React from 'react';
import Digit from './Digit';
import Input from './Input';

class Calculator extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			value1: null,
			value2: null,
			currentVal: null,
			answer: null,
			isSecond: false
		};
	}

	updateValue = (value) => {
		this.setState({
			currentVal: value
		});
		console.log(this.state.currentVal);
	}

	render() {
		return (
			<div>
				<Input />
				<Digit digit = {1} onClick={() => this.updateValue(value)} />
				<Digit digit = {2} onClick={() => this.updateValue(value)} />
				<Digit digit = {3} onClick={() => this.updateValue(value)} />
			    <Digit digit = {4} onClick={() => this.updateValue(value)} />
			    <Digit digit = {5} onClick={() => this.updateValue(value)} />
			    <Digit digit = {6} />
				<Digit digit = {7} />
				<Digit digit = {8} />
				<Digit digit = {9} />
				<Digit digit = {0} />
			</div>
		);
	}
}

export default Calculator;
