import React from 'react';

class Input extends React.Component {
	constructor(props) {
		super(props);
		this.input = React.createRef();
	}

	componentDidUpdate(prevProps) {
		if(this.props.isSecond === false) {
			if(this.props.valueone !== prevProps.valueone) {
				this.input.current.value = this.props.valueone;
			}
		}
		else {
			if(this.props.valuetwo !== prevProps.valuetwo) {
				this.input.current.value = this.props.valuetwo;
			}
		}
	}

	renderInput() {
		return <input type="text" value={this.valueone} />;
	}

	render() {
		return (
			<div>
				<form className="ui form">
					<div className="field">
						{this.renderInput()}
					</div>
				</form>
			</div>
		);
	}
}

export default Input;
