import React from 'react';

class Input extends React.Component {
	constructor(props) {
		super(props);
		this.input = React.createRef();
	}

	componentDidMount() {
		this.input = React.createRef();
		if(this.props.isSecond === false) {
			this.input.currentValue = this.props.valueone;
		}
		else {
			this.input.currentValue = this.props.valuetwo;
		}
	}

	render() {
		return (
			<div>
				<form className="ui form">
					<div className="field">
						<input type="text" ref={this.input} />
					</div>
				</form>
			</div>
		);
	}
}

export default Input;
