		this.setState({
			currentVal: value
		});
		console.log(this.state.currentVal);
	}

	setOperation = (operation) => {
		if(operation === '+' && this.state.isSecond === false) {
			this.setState({
				isSecond: true
			});
		}
		else if(operation === '-' && this.state.isSecond === false) {
			this.setState({
				isSecond: true
			});
		}
		else if(operation === '*' && this.state.isSecond === false) {
			this.setState({
