import React from 'react';
import Digit from './Digit';
import Input from './Input';
import Operation from './Operation';

class Calculator extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			valueone: 0,
			valuetwo: 0,
			answer: 0,
			multiplier: 1,
			isSecond: false,
			isOperation: null
		};
	}

	updateValue = (value) => {
		if(this.state.isSecond === false) {
			this.setState((state) => ({
				valueone: state.valueone * state.multiplier
			}));
			this.setState((state) => ({
				valueone: state.valueone + value
			}));
		}
		else {
			this.setState((state) => ({
				valuetwo: state.valuetwo * state.multiplier
			}));
			this.setState((state) => ({
				valuetwo: state.valuetwo + value
			}));
		}
		this.setState ((state) => ({
			multiplier: state.multiplier * 10
		}));
	}

	checkAnswer = () => {
		alert(this.state.answer);
	}

	setOperation = (operation) => {
		if(operation === '+' && this.state.isSecond === false) {
			this.setState({
				isOperation: 'addition',
				isSecond: true,
				multiplier: 1
			});
		}
		else if(operation === '-' && this.state.isSecond === false) {
			this.setState({
				isOperation: 'subtraction',
				isSecond: true,
				multiplier: 1
			});
		}
		else if(operation === '*' && this.state.isSecond === false) {
			this.setState({
				isOperation: 'multiplication',
				isSecond: true,
				multiplier: 1	
			});
		}
		else if(operation === '/' && this.state.isSecond === false) {
			this.setState({
				isOperation: 'division',
				isSecond: true,
				multiplier: 1
			});
		}
		else if(operation === 'sqrt' && this.state.isSecond === false) {
			this.setState ((state) => ({
				answer: Math.sqrt(state.valueone)
			}));
			this.setState({
				valueone: 0,
				answer: 0,
				multiplier: 1
			});
		}
		else if(operation === '^' && this.state.isSecond === false) {

		}
		else if(operation === '=' && this.state.isSecond === true) {
			if(this.state.isOperation === 'addition') {
				this.setState ((state) => ({
					answer: state.valueone + state.valuetwo
				}));
			}
			else if(this.state.isOperation === 'subtraction') {
				this.setState ((state) => ({
					answer: state.valueone - state.valuetwo
				}));
			}
			else if(this.state.isOperation === 'multiplication') {
				this.setState ((state) => ({
					answer: state.valueone * state.valuetwo
				}));
			}
			else if(this.state.isOperation === 'division') {
				this.setState ((state) => ({
					answer: state.valueone / state.valuetwo
				}));
			}
			this.setState({
				isSecond: false,
				multiplier: 1,
				valueone: 0,
				valuetwo: 0,
				answer: 0,
				isOperation: null
			});
		}
	}

	renderInput() {
		return (
		    <Input 
			/>
		);
	}

	render() {
		return (
			<div>				
				{this.renderInput()}
				<Operation operation = {'Check answer'} onClick={() => this.checkAnswer()} />
				<div className="ui buttons">
					<Digit digit = {1} onClick={() => this.updateValue(1)} />
					<Digit digit = {2} onClick={() => this.updateValue(2)} />
					<Digit digit = {3} onClick={() => this.updateValue(3)} />
				</div>
				<br/>
				<div className="ui buttons">
			    	<Digit digit = {4} onClick={() => this.updateValue(4)} />
			    	<Digit digit = {5} onClick={() => this.updateValue(5)} />
			    	<Digit digit = {6} onClick={() => this.updateValue(6)} />
			    </div>
			    <br/>
			    <div className="ui buttons">
					<Digit digit = {7} onClick={() => this.updateValue(7)} />
					<Digit digit = {8} onClick={() => this.updateValue(8)} />
					<Digit digit = {9} onClick={() => this.updateValue(9)} />
				</div>
				<br/>
				<div className="ui buttons">
					<Digit digit = {0} onClick={() => this.updateValue(0)} />
					<Operation operation = {'+'} onClick={() => this.setOperation('+')}  />
					<Operation operation = {'-'} onClick={() => this.setOperation('-')}  />
				</div>
				<br/>
				<div className="ui buttons">
					<Operation operation = {'*'} onClick={() => this.setOperation('*')}  />
					<Operation operation = {'/'} onClick={() => this.setOperation('/')}  />
					<Operation operation = {'='} onClick={() => this.setOperation('=')}  />
				</div>
				<br/>
				<div className="ui buttons">
					<Operation operation = {'sqrt'} onClick={() => this.setOperation('sqrt')}  />
				</div>
			</div>
		);
	}
}

export default Calculator;
